CREATE TABLE `customers` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `code` varchar(255) DEFAULT NULL,
  `name` varchar(50) NOT NULL,
  `display_name` varchar(100) DEFAULT NULL,
  `phone` varchar(30) DEFAULT NULL,
  `email` varchar(50) DEFAULT NULL,
  `birthday` date DEFAULT NULL,
  `position` varchar(255) DEFAULT NULL COMMENT 'chức vụ',
  `company_name` varchar(255) DEFAULT NULL COMMENT 'tên công ty',
  `brand_name` varchar(255) DEFAULT NULL COMMENT 'tên thương hiệu',
  `company_address` varchar(255) DEFAULT NULL,
  `link` text,
  `field` varchar(255) DEFAULT NULL COMMENT 'Ngành hàng',
  `branch` varchar(255) DEFAULT NULL COMMENT 'Thể loại',
  `active` tinyint(4) DEFAULT '1',
  `state` varchar(255) DEFAULT 'DRAFT' COMMENT 'Tình trạng khách hàng',
  `contract_state` varchar(255) DEFAULT NULL COMMENT 'Trạng thái hợp đồng',
  `payment_state` varchar(255) DEFAULT NULL COMMENT 'Trạng thái thanh toán',
  `sign_date` datetime DEFAULT NULL COMMENT 'Ngày ký hợp đồng',
  `describe` text COMMENT 'Mô tả khách hàng',
  `source` varchar(255) DEFAULT NULL COMMENT 'Nguồn khai thác',
  `demand` text COMMENT 'Nhu cầu',
  `concern` text COMMENT 'Lo lắng',
  `user_assigned_id` int(10) unsigned NOT NULL DEFAULT '1',
  `user_created_id` int(10) unsigned NOT NULL DEFAULT '1',
  `upload_dir` varchar(255) DEFAULT NULL,
  `note` text,
  `created_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `state_changed_at` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `priority` varchar(255) DEFAULT 'NONE',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `user_created_id` (`user_created_id`) USING BTREE,
  KEY `user_assigned_id` (`user_assigned_id`) USING BTREE,
  CONSTRAINT `customers_ibfk_2` FOREIGN KEY (`user_created_id`) REFERENCES `users` (`id`),
  CONSTRAINT `customers_ibfk_3` FOREIGN KEY (`user_assigned_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=392 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=DYNAMIC;
